package com.example.sim.category;


import android.content.Intent;
import android.graphics.Bitmap;
import android.net.Uri;
import android.os.Bundle;
import android.util.Base64;
import android.view.View;
import android.widget.Button;
import android.widget.ImageView;

import com.example.sim.BaseActivity;
import com.example.sim.MainActivity;
import com.example.sim.R;
import com.example.sim.dto.category.CategoryCreateDTO;
import com.example.sim.dto.category.CategoryItemDTO;
import com.example.sim.service.CategoryNetwork;
import com.google.android.material.textfield.TextInputEditText;
import com.google.android.material.textfield.TextInputLayout;
import com.oginotihiro.cropview.CropView;

import java.io.ByteArrayOutputStream;
import java.util.List;

import retrofit2.Call;
import retrofit2.Callback;
import retrofit2.Response;

public class CategoryCreateActivity extends BaseActivity {

    ImageView avatar_image;
    CropView cropView;
    CategoryCreateDTO categoryTest=null;

    // Activityies
    Intent mainActivity;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_category_create);

        avatar_image = findViewById(R.id.my_avatar);
        Button selectAvatar_btn = findViewById(R.id.btn_SelectFoto);
        Button addCategory_btn = findViewById(R.id.btn_Category_Add);
        cropView = (CropView) findViewById(R.id.cropView);
        //Додаванна Активіті на яку перейду
        mainActivity = new Intent(this, MainActivity.class);
        mainActivity.addFlags(Intent.FLAG_ACTIVITY_CLEAR_TOP | Intent.FLAG_ACTIVITY_SINGLE_TOP);

        //Додати категорію
        addCategory_btn.setOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View view) {
                System.out.println("-----Відправка данних на сервер. Створення нової Категорії");
                categoryTest = new CategoryCreateDTO();
//                 String imageBase64_part1= "data:image/jpeg;base64,";
//                 String imageBase64_part2= "";
//                 CategoryCreateDTO testDto = new CategoryCreateDTO("Іграшки",imageBase64_part1+imageBase64_part2,1,"Іграшки для дітей");

                avatar_image.buildDrawingCache();
                Bitmap bitmap = avatar_image.getDrawingCache();

                ByteArrayOutputStream stream=new ByteArrayOutputStream();
                bitmap.compress(Bitmap.CompressFormat.JPEG, 90, stream);
                byte[] image=stream.toByteArray();
               // System.out.println("byte array:"+image);

                String img_str = Base64.encodeToString(image, 0);
                String imageToLoad = "data:image/jpeg;base64,"+img_str;
//                System.out.println(imageToLoad);


                TextInputLayout name_TextLayout = findViewById(R.id.name_textField);
                String name = name_TextLayout.getEditText().getText().toString();

                TextInputLayout priority_TextLayout = findViewById(R.id.priority_textField);
                int priority = Integer.parseInt(priority_TextLayout.getEditText().getText().toString());

                TextInputLayout desc_TextLayout = findViewById(R.id.description_textField);
                String desc = desc_TextLayout.getEditText().getText().toString();

                // Create DTO
                categoryTest.setName(name);
                categoryTest.setImageBase64("imageToLoad");
                categoryTest.setPriority(priority);
                categoryTest.setDescription(desc);
                System.out.println(categoryTest);

                requestServer(categoryTest);
            }
        });

        //Обрати фото
        selectAvatar_btn.setOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View view) {
                Intent getIntent = new Intent(Intent.ACTION_GET_CONTENT);
                getIntent.setType("image/*");

                Intent pickIntent = new Intent(Intent.ACTION_PICK, android.provider.MediaStore.Images.Media.EXTERNAL_CONTENT_URI);
                pickIntent.setType("image/*");

                Intent chooserIntent = Intent.createChooser(getIntent, "Select Image");
                chooserIntent.putExtra(Intent.EXTRA_INITIAL_INTENTS, new Intent[] {pickIntent});

                startActivityForResult(chooserIntent, 1);
            }
        });
    }

    @Override
    protected void onActivityResult(int requestCode, int resultCode, Intent data) {
        super.onActivityResult(requestCode, resultCode, data);

        if (requestCode == 1) {
            Uri selectedImageUri = data.getData();
//            cropView.of(selectedImageUri)
//                    .withAspect(50, 50)
//                    .initialize(CategoryCreateActivity.this);

            avatar_image.setImageURI(selectedImageUri);
        }
    }

    void requestServer(CategoryCreateDTO model) {
        CategoryNetwork
                .getInstance()
                .getJsonApi()
                .addCategory(model)
                .enqueue(new Callback<CategoryCreateDTO>() {
                    @Override
                    public void onResponse(Call<CategoryCreateDTO> call, Response<CategoryCreateDTO> response) {
                        if (response.isSuccessful())
                        {
                            System.out.println("----Category Added successfuly");
                            startActivity(mainActivity);
                        }
                        else{
                            System.out.println("Server erroe");
                        }
                    }

                    @Override
                    public void onFailure(Call<CategoryCreateDTO> call, Throwable t) {
                        System.out.println("----Category added Error");

                    }
                });
    }
}